#!/bin/bash
docker run -d --name "ps_17" -e "POSTGRES_USER=gedgar" -e "POSTGRES_PASSWORD=$POSTGRES_PASSWORD" -e "POSTGRES_DB=graphdb" -e "EXTERNAL_STORAGE_PATH=/home/postgres/postgres_data" -e "EXTERNAL_TABLESPACE_NAME=ext_tablespace" -e "PATH=/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin:/usr/lib/postgresql/17/bin" -e "GOSU_VERSION=1.17" -e "LANG=en_US.utf8" -e "PG_MAJOR=17" -e "PG_VERSION=17.4-1.pgdg120+2" -e "PGDATA=/var/lib/postgresql/data"  -p 5432:5432  -v "/home/gedgar/postgres_data:/home/postgres/postgres_data" -v "pg_data_volume:/var/lib/postgresql/data"  --restart no --entrypoint "docker-entrypoint.sh" "postgres" "postgres" 
